<script>
  import Background from './BackgroundEffect.vue';
  import { ref, onMounted, onUnmounted } from 'vue';

  export default {
    components: {
      Background
    },
    setup() {
   
      const isScrolled = ref(false);
      const isDarkMode = ref(false);
      const handleScroll = () => {
        isScrolled.value = window.scrollY > 0;
      };

      onMounted(() => {
        const savedDarkMode = localStorage.getItem('darkMode');
        if (savedDarkMode !== null) {
          isDarkMode.value = savedDarkMode === 'true';
          document.documentElement.classList.toggle('dark', isDarkMode.value);
          const appDiv = document.getElementById('app');
          if (appDiv) {
            if (isDarkMode.value) {
              appDiv.classList.add('dark:bg-slate-800');
              document.body.classList.add('dark:bg-slate-800');
            } else {
              appDiv.classList.remove('dark:bg-slate-800');
              document.body.classList.remove('dark:bg-slate-800');
            }
            appDiv.classList.toggle('dark', isDarkMode.value);
            document.body.classList.toggle('dark', isDarkMode.value);
          }
        }
        window.addEventListener('scroll', handleScroll);
      });

      onUnmounted(() => {
        window.removeEventListener('scroll', handleScroll);
      });

      const toggleTheme = () => {
        isDarkMode.value = !isDarkMode.value;
        document.documentElement.classList.toggle('dark', isDarkMode.value);
        const appDiv = document.getElementById('app');
        if (appDiv) {
          if (isDarkMode.value) {
            appDiv.classList.add('dark:bg-slate-800');
            document.body.classList.add('dark:bg-slate-800');
          } else {
            appDiv.classList.remove('dark:bg-slate-800');
            document.body.classList.remove('dark:bg-slate-800');
          }
          appDiv.classList.toggle('dark', isDarkMode.value);
          document.body.classList.toggle('dark', isDarkMode.value);
        }
        localStorage.setItem('darkMode', isDarkMode.value.toString());
      };

      const isToggledProject = ref(false);

      const showProject = () => {
        isToggledProject.value = !isToggledProject.value;
      };

      return {
        isScrolled,
        isDarkMode,
        toggleTheme,
        isToggledProject,
        showProject,
      };
    },
  }
</script>

